// ─── Helper: resolve creature ID → image path ───

export function getCreatureImage(id: string): string | null {
  if (!id) return null;
  const shortId = id.includes("-") ? id.replace(/^[a-z]{2}-/, "") : id;
  return CREATURE_IMAGE_MAP[shortId] || null;
}

