// ─── Helpers ───

export function getAllCreatures(): (Creature & { country: string; region: string; countryCode: string })[] {
  const result: (Creature & { country: string; region: string; countryCode: string })[] = [];
  for (const country of FOLKLORE_DATA) {
    for (const creature of country.b) {
      result.push({
        ...creature,
        country: country.c,
        region: country.r,
        countryCode: country.i,
      });
    }
  }
  return result;
}

export function getCreatureById(id: string) {
  for (const country of FOLKLORE_DATA) {
    for (const creature of country.b) {
      if (creature.id === id) {
        return {
          ...creature,
          country: country.c,
          region: country.r,
          countryCode: country.i,
        };
      }
    }
  }
  return null;
}

// ─── Region / Country helpers ───

export function getAllRegions(): string[] {
  const set = new Set<string>();
  for (const c of FOLKLORE_DATA) set.add(c.r);
  return Array.from(set).sort();
}

export function getAllCountries(): { code: string; name: string; region: string }[] {
  return FOLKLORE_DATA.map((c) => ({ code: c.i, name: c.c, region: c.r }));
}

export function getCreaturesByRegion(region: string) {
  return getAllCreatures().filter((c) => c.region === region);
}

export function getCreaturesByCountryCode(code: string) {
  return getAllCreatures().filter((c) => c.countryCode === code.toUpperCase());
}

export function getCountryByCode(code: string): { name: string; region: string } | null {
  const upper = code.toUpperCase();
  const found = FOLKLORE_DATA.find((c) => c.i === upper);
  return found ? { name: found.c, region: found.r } : null;
}

export function regionToSlug(region: string): string {
  return region.toLowerCase().replace(/\s+/g, "-");
}

export function slugToRegion(slug: string): string | null {
  const regions = getAllRegions();
  return regions.find((r) => regionToSlug(r) === slug) || null;
}
